import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import Chart from 'chart.js/auto';
export default {
  name: "EmployeeDashboard",
  data() {
    return {
      employees: [],
      loading: true,
      error: null,
      deptBar: null,
      salaryBar: null,
      historyBar: null
    };
  },
  mounted() {
    fetch("https://zakariad007.github.io/employee/employee_info.json").then(res => res.json()).then(data => {
      this.employees = data.employeeInformation;
      this.loading = false;
      this.$nextTick(() => {
        this.renderCharts();
      });
    }).catch(() => {
      this.error = "Failed to load employee data.";
      this.loading = false;
    });
  },
  methods: {
    renderCharts() {
      if (this.deptBar) {
        this.deptBar.destroy();
        this.deptBar = null;
      }
      if (this.salaryBar) {
        this.salaryBar.destroy();
        this.salaryBar = null;
      }
      if (this.historyBar) {
        this.historyBar.destroy();
        this.historyBar = null;
      }
      const deptCounts = {};
      this.employees.forEach(emp => {
        if (emp.department) {
          deptCounts[emp.department] = (deptCounts[emp.department] || 0) + 1;
        }
      });
      const deptCtx = this.$refs.deptBar;
      if (deptCtx) {
        this.deptBar = new Chart(deptCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(deptCounts),
            datasets: [{
              label: 'Employees',
              data: Object.values(deptCounts),
              backgroundColor: '#60a5fa',
              borderColor: '#2563eb',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                text: 'Employees per Department',
                font: {
                  size: 16,
                  weight: 'bold'
                }
              },
              tooltip: {
                enabled: true,
                backgroundColor: '#fff',
                titleColor: '#111',
                bodyColor: '#111',
                borderColor: '#2563eb',
                borderWidth: 1
              }
            },
            scales: {
              x: {
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  display: false
                }
              },
              y: {
                beginAtZero: true,
                precision: 0,
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  color: '#e5e7eb'
                }
              }
            }
          }
        });
      }
      const salaryRanges = {
        '50k-59k': 0,
        '60k-69k': 0,
        '70k-79k': 0,
        '80k+': 0
      };
      this.employees.forEach(emp => {
        if (emp.salary < 60000) salaryRanges['50k-59k']++;else if (emp.salary < 70000) salaryRanges['60k-69k']++;else if (emp.salary < 80000) salaryRanges['70k-79k']++;else salaryRanges['80k+']++;
      });
      const salaryCtx = this.$refs.salaryBar;
      if (salaryCtx) {
        this.salaryBar = new Chart(salaryCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(salaryRanges),
            datasets: [{
              label: 'Employees',
              data: Object.values(salaryRanges),
              backgroundColor: '#34d399',
              borderColor: '#059669',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                text: 'Salary Range Distribution',
                font: {
                  size: 16,
                  weight: 'bold'
                }
              },
              tooltip: {
                enabled: true,
                backgroundColor: '#fff',
                titleColor: '#111',
                bodyColor: '#111',
                borderColor: '#059669',
                borderWidth: 1
              }
            },
            scales: {
              x: {
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  display: false
                }
              },
              y: {
                beginAtZero: true,
                precision: 0,
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  color: '#e5e7eb'
                }
              }
            }
          }
        });
      }

      // Employment History (Year Joined)
      const yearCounts = {};
      this.employees.forEach(emp => {
        const match = emp.employmentHistory && emp.employmentHistory.match(/Joined in (\d{4})/);
        if (match) {
          const year = match[1];
          yearCounts[year] = (yearCounts[year] || 0) + 1;
        }
      });
      const historyCtx = this.$refs.historyBar;
      if (historyCtx) {
        this.historyBar = new Chart(historyCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(yearCounts),
            datasets: [{
              label: 'Employees Joined',
              data: Object.values(yearCounts),
              backgroundColor: '#f59e42',
              borderColor: '#b45309',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                text: 'Employees Joined Per Year',
                font: {
                  size: 16,
                  weight: 'bold'
                }
              },
              tooltip: {
                enabled: true,
                backgroundColor: '#fff',
                titleColor: '#111',
                bodyColor: '#111',
                borderColor: '#b45309',
                borderWidth: 1
              }
            },
            scales: {
              x: {
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  display: false
                }
              },
              y: {
                beginAtZero: true,
                precision: 0,
                ticks: {
                  font: {
                    size: 12
                  }
                },
                grid: {
                  color: '#e5e7eb'
                }
              }
            }
          }
        });
      }
    }
  }
};
