import AttendanceComp from '@/components/AttendanceComp.vue';
export default {
  name: 'AttendanceView',
  components: {
    AttendanceComp
  },
  mounted() {
    // Attach the Shadow DOM to the div
    const shadowRoot = this.$refs.shadowHost.attachShadow({
      mode: 'open'
    });

    // Dynamically load Bootstrap CSS inside the Shadow DOM
    const bootstrapLink = document.createElement('link');
    bootstrapLink.rel = 'stylesheet';
    bootstrapLink.href = 'https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css';
    shadowRoot.appendChild(bootstrapLink);

    // Add a container div where we will mount the Vue component
    const container = document.createElement('div');
    shadowRoot.appendChild(container);

    // Mount the Vue component (AttendanceComp) to the container inside Shadow DOM
    const vm = new Vue({
      render: h => h(AttendanceComp)
    });

    // Mount the Vue instance to the container inside the Shadow DOM
    vm.$mount(container);

    // Append custom styles for this component
    const style = document.createElement('style');
    style.textContent = `
      .attendance-header {
        background-color: #39BBC8 !important;
      }
      .card {
        border-radius: 0.5rem;
      }
      .card-header {
        border-radius: 0.5rem 0.5rem 0 0 !important;
      }
    `;
    shadowRoot.appendChild(style);
  }
};
