import AttendanceComp from '@/components/AttendanceComp.vue';
export default {
  name: 'AttendanceView',
  components: {
    AttendanceComp
  },
  mounted() {
    // Attach a Shadow DOM to the div
    const shadowRoot = this.$refs.shadowHost.attachShadow({
      mode: 'open'
    });

    // Dynamically load Bootstrap CSS inside the Shadow DOM
    const bootstrapLink = document.createElement('link');
    bootstrapLink.rel = 'stylesheet';
    bootstrapLink.href = 'https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css';
    shadowRoot.appendChild(bootstrapLink);

    // Add the component's HTML inside the Shadow DOM
    const container = document.createElement('div');
    container.innerHTML = `
      <div class="container mt-4">
        <div class="row justify-content-center">
          <div class="col-lg-10">
            <div class="card shadow-sm">
              <div class="card-header text-white attendance-header">
                <div class="d-flex justify-content-between align-items-center">
                  <h2 class="h5 mb-0">Attendance</h2>
                </div>
              </div>
              <div class="card-body">
                <attendance-comp></attendance-comp>
              </div>
            </div>
          </div>
        </div>
      </div>
    `;
    shadowRoot.appendChild(container);

    // Add scoped styles for the shadow DOM
    const style = document.createElement('style');
    style.textContent = `
      .attendance-header {
        background-color: #39BBC8 !important;
      }
      .card {
        border-radius: 0.5rem;
      }
      .card-header {
        border-radius: 0.5rem 0.5rem 0 0 !important;
      }
    `;
    shadowRoot.appendChild(style);
  }
};
