import { createRouter, createWebHistory } from 'vue-router'
import DashboardView from '@/views/DashboardView.vue'
import Employee_DirectoryView from '@/views/Employee_DirectoryView.vue'
import PayrollView from '@/views/PayrollView.vue'
import LoginView from '@/views/LoginView.vue'
import TimeAttendanceView from '@/views/Time-AttendanceView.vue'
import PerformanceReviewView from '@/views/PerformanceReviewView.vue'

const routes = [
  {
    path: '/',
    redirect: '/login'
  },
  {
    path: '/login',
    name: 'Login',
    component: LoginView
  },
  {
    path: '/dashboard',
    name: 'Dashboard',
    component: DashboardView
  },
  {
    path: '/employee directory',
    name: 'Employee Directory',
    component: Employee_DirectoryView
  },
  {
    path: '/payroll',
    name: 'Payroll',
    component: PayrollView
  },
  {
    path: '/time-attendance',
    name: 'Time Attendance',
    component: TimeAttendanceView
  },
  {
    path: '/performance-review',
    name: 'Performance Reviews',
    component: PerformanceReviewView
  },
  {
    path: '/about',
    name: 'about',
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue')
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
