import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.flat-map.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import "core-js/modules/es.iterator.some.js";
import "core-js/modules/es.set.difference.v2.js";
import "core-js/modules/es.set.intersection.v2.js";
import "core-js/modules/es.set.is-disjoint-from.v2.js";
import "core-js/modules/es.set.is-subset-of.v2.js";
import "core-js/modules/es.set.is-superset-of.v2.js";
import "core-js/modules/es.set.symmetric-difference.v2.js";
import "core-js/modules/es.set.union.v2.js";
import Chart from 'chart.js/auto';
export default {
  name: "EmployeeDashboard",
  components: {
    DashboardCard: {
      props: ['title', 'value', 'color'],
      template: `
        <div class="bg-white rounded-lg shadow p-4 flex flex-col items-center w-40 min-w-[140px]">
          <div class="text-base font-medium mb-1" :style="{color: color}">{{ title }}</div>
          <div class="text-2xl font-bold" :style="{color: color}">{{ value }}</div>
        </div>
      `
    },
    DashboardChart: {
      props: ['title', 'refName'],
      template: `
        <div class="bg-white rounded-lg shadow p-4 flex flex-col items-center w-full">
          <h2 class="text-lg font-medium mb-2">{{ title }}</h2>
          <canvas :ref="refName" width="250" height="200"></canvas>
        </div>
      `
    }
  },
  data() {
    return {
      employees: [],
      loading: true,
      error: null,
      deptBar: null,
      salaryBar: null,
      historyBar: null,
      hoursLine: null,
      search: "",
      selectedDepartment: "",
      payrollData: [],
      attendanceData: [],
      totalEmployees: 0,
      totalPresent: 0,
      totalAbsent: 0,
      totalPending: 0,
      totalDenied: 0
    };
  },
  computed: {
    departments() {
      const depts = this.employees.map(e => e.department).filter(Boolean);
      return [...new Set(depts)];
    },
    filteredEmployees() {
      return this.employees.filter(emp => {
        return this.selectedDepartment ? emp.department === this.selectedDepartment : true;
      });
    }
  },
  watch: {
    filteredEmployees() {
      this.$nextTick(() => {
        this.destroyCharts();
        if (this.filteredEmployees.length) {
          this.renderCharts();
          this.renderHoursLine();
        }
      });
    }
  },
  mounted() {
    fetch("https://zakariad007.github.io/employee/employee_info.json").then(res => res.json()).then(data => {
      this.employees = data.employeeInformation;
      this.loading = false;
      return fetch("https://zakariad007.github.io/payroll/payroll_data.json");
    }).then(res => res.json()).then(payroll => {
      this.payrollData = payroll.payrollData || [];
      this.$nextTick(() => {
        if (this.$refs.hoursLine) {
          this.renderHoursLine();
        }
      });
    }).catch(() => {
      this.error = "Failed to load employee or payroll data.";
      this.loading = false;
    });
    fetch("https://zakariad007.github.io/attendance/attendance.json").then(res => res.json()).then(data => {
      this.attendanceData = data.attendanceAndLeave || [];
      this.calculateAttendanceStats();
    });
  },
  methods: {
    destroyCharts() {
      if (this.deptBar) {
        this.deptBar.destroy();
        this.deptBar = null;
      }
      if (this.salaryBar) {
        this.salaryBar.destroy();
        this.salaryBar = null;
      }
      if (this.historyBar) {
        this.historyBar.destroy();
        this.historyBar = null;
      }
      if (this.hoursLine) {
        this.hoursLine.destroy();
        this.hoursLine = null;
      }
    },
    renderCharts() {
      if (!this.filteredEmployees.length) return;
      // Employees per Department
      const deptCounts = {};
      this.filteredEmployees.forEach(emp => {
        if (emp.department) {
          deptCounts[emp.department] = (deptCounts[emp.department] || 0) + 1;
        }
      });
      const deptCtx = this.$refs.deptBar;
      if (deptCtx && Object.keys(deptCounts).length) {
        this.deptBar = new Chart(deptCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(deptCounts),
            datasets: [{
              label: 'Employees',
              data: Object.values(deptCounts),
              backgroundColor: '#60a5fa',
              borderColor: '#2563eb',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                font: {
                  size: 16,
                  weight: 'bold'
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                precision: 0
              }
            }
          }
        });
      }
      // Salary Range Distribution
      const salaryRanges = {
        '50k-59k': 0,
        '60k-69k': 0,
        '70k-79k': 0,
        '80k+': 0
      };
      this.filteredEmployees.forEach(emp => {
        if (emp.salary < 60000) salaryRanges['50k-59k']++;else if (emp.salary < 70000) salaryRanges['60k-69k']++;else if (emp.salary < 80000) salaryRanges['70k-79k']++;else salaryRanges['80k+']++;
      });
      const salaryCtx = this.$refs.salaryBar;
      if (salaryCtx && Object.values(salaryRanges).some(v => v > 0)) {
        this.salaryBar = new Chart(salaryCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(salaryRanges),
            datasets: [{
              label: 'Employees',
              data: Object.values(salaryRanges),
              backgroundColor: '#34d399',
              borderColor: '#059669',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                font: {
                  size: 16,
                  weight: 'bold'
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                precision: 0
              }
            }
          }
        });
      }
      // Employees Joined Per Year
      const yearCounts = {};
      this.filteredEmployees.forEach(emp => {
        const match = emp.employmentHistory && emp.employmentHistory.match(/Joined in (\d{4})/);
        if (match) {
          const year = match[1];
          yearCounts[year] = (yearCounts[year] || 0) + 1;
        }
      });
      const historyCtx = this.$refs.historyBar;
      if (historyCtx && Object.keys(yearCounts).length) {
        this.historyBar = new Chart(historyCtx, {
          type: 'bar',
          data: {
            labels: Object.keys(yearCounts),
            datasets: [{
              label: 'Employees Joined',
              data: Object.values(yearCounts),
              backgroundColor: '#f59e42',
              borderColor: '#b45309',
              borderWidth: 2,
              borderRadius: 6,
              barPercentage: 0.7,
              categoryPercentage: 0.7
            }]
          },
          options: {
            responsive: false,
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                font: {
                  size: 16,
                  weight: 'bold'
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                precision: 0
              }
            }
          }
        });
      }
    },
    renderHoursLine() {
      if (this.hoursLine) {
        this.hoursLine.destroy();
        this.hoursLine = null;
      }
      const ctx = this.$refs.hoursLine;
      if (!ctx || !this.payrollData.length) return;
      const displayMonths = [{
        key: "2025-06-01",
        label: "Week 1"
      }, {
        key: "2025-06-08",
        label: "Week 2"
      }, {
        key: "2025-06-15",
        label: "Week 3"
      }, {
        key: "2025-06-22",
        label: "Week 4"
      }, {
        key: "2025-06-29",
        label: "Week 5"
      }];
      const totalHours = Number(this.payrollData.reduce((sum, entry) => sum + (typeof entry.hoursWorked === "number" ? entry.hoursWorked : 0), 0));
      const monthHours = [1640, 1358, 1254, 1516, totalHours || 0];
      const monthLabels = displayMonths.map(m => m.label);
      this.hoursLine = new Chart(ctx, {
        type: 'line',
        data: {
          labels: monthLabels,
          datasets: [{
            label: 'Total Hours Worked',
            data: monthHours,
            fill: true,
            borderColor: '#6366f1',
            backgroundColor: 'rgba(99,102,241,0.1)',
            tension: 0.4,
            pointBackgroundColor: '#6366f1',
            pointBorderColor: '#fff',
            pointRadius: 4
          }]
        },
        options: {
          responsive: false,
          plugins: {
            legend: {
              display: true
            },
            title: {
              display: true,
              font: {
                size: 16,
                weight: 'bold'
              }
            }
          },
          scales: {
            x: {
              title: {
                display: true,
                text: 'Weeks'
              }
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Hours Worked'
              }
            }
          }
        }
      });
    },
    calculateAttendanceStats() {
      this.totalEmployees = this.attendanceData.length;
      const allAttendance = this.attendanceData.flatMap(emp => emp.attendance || []);
      this.totalPresent = allAttendance.filter(a => a.status && a.status.trim().toLowerCase() === "present").length;
      this.totalAbsent = allAttendance.filter(a => a.status && a.status.trim().toLowerCase() === "absent").length;
      const allLeaveRequests = this.attendanceData.flatMap(emp => emp.leaveRequests || []);
      this.totalPending = allLeaveRequests.filter(l => l.status && l.status.trim().toLowerCase() === "pending").length;
      this.totalDenied = allLeaveRequests.filter(l => l.status && l.status.trim().toLowerCase() === "denied").length;
    }
  }
};
